        return new Source("{% extends 'base.html.twig' %}

{% block title %}Liste de Epreuves{% endblock %}

{% block body %}

    <style>
        .example-wrapper {
            margin: 1em auto;
            max-width: 900px;
            width: 95%;
            font: 18px/1.5 sans-serif;
        }

        .example-wrapper code {
            background: #F5F5F5;
            padding: 2px 6px;
        }
    </style>

    <div class=\"example-wrapper\">
        {# <h1>Hello {{ athletes }}! âœ…</h1> #}
        <div class=\"table-responsive\">
            <table class=\"table table-striped table-bordered table-hover table-condensed\">
                <thead>
                    <tr>
                        <th>id epreuve</th>
                        <th>nom epreuve</th>
                        <th>description</th>
                        <th>heure epreuve</th>
                        <th>medaille epreuve</th>
                        <th>id competition</th>
                        <th>id categorie</th>
                    </tr>
                </thead>
                <tbody>
                    {% for epreuve in epreuves %}
                        <tr>
                            <td>{{ epreuve.getId() }}</td>
                            <td>{{ epreuve.getNomEpreuve() }}</td>
                            <td>{{ epreuve.getDescription() }}</td>
                            <td>{{ epreuve.getHeureEpreuve() }}</td>
                            <td>{{ epreuve.getMedailleEpreuve() }}</td>
                            <td>{{ epreuve.getIdCompetition() }}</td>
                            <td>{{ epreuve.getIdCategorie() }}</td>
                        </tr>
                    {% endfor %}
                </tbody>
            </table>
        </div>
    </div>

{% endblock %}
